'use strict';

/**
 * 合并对象\
 * @param  obj   obj1        要合并的原始对象
 * @param  obj   obj2        待合并的新对象
 *
 * @author wangkun5 <wangkun5@staff.sina.com.cn>
 * @date   2015-12-29
 */
exports.objMerge = function(obj1, obj2) {
    for (var p in obj2) {
        try {
            // Property in destination object set; update its value.
            if (obj2[p].constructor == Object) {
                obj1[p] = this.objMerge(obj1[p], obj2[p]);

            } else {
                obj1[p] = obj2[p];
            }
        } catch (e) {
            // Property in destination object not set; create it and set its value.
            obj1[p] = obj2[p];
        }
    }
    return obj1;
};